import React, { Component } from 'react'
import firebaseConfig from '../firebaseConfig';
import * as firebaseui from "firebaseui";
import firebase from "firebase";
class PhoneButton extends Component {
  componentDidMount() {
    const fbase = firebase.initializeApp(firebaseConfig);
    const uiConfig = {
      signInSuccessUrl: "/", //This URL is used to return to that page when we got success response for phone authentication.
      signInOptions: [
        {
          provider: 'microsoft.com',
          providerName: 'Microsoft',
          fullLabel: true,
          // To override the full label of the button.
          // fullLabel: 'Login with Microsoft',
          buttonColor: '#2F2F2F',
          iconUrl: '<icon-url-of-sign-in-button>',
          loginHintKey: 'login_hint',
          scopes: [
            'mail.read'
          ],
          customParameters: {
            prompt: 'consent'
          }
        },
        firebase.auth.PhoneAuthProvider.PROVIDER_ID,
        firebase.auth.GoogleAuthProvider.PROVIDER_ID,
        firebase.auth.FacebookAuthProvider.PROVIDER_ID,
      ],
      tosUrl: "https://netflix-clone-ankur.herokuapp.com/"
    };
    var ui = new firebaseui.auth.AuthUI(firebase.auth());
    ui.start("#firebaseui-auth-container", uiConfig);
  }
  render() {
    return (
      <div>
        <h1 >OTP</h1>
        <div id="firebaseui-auth-container"></div>
      </div>
    )
  }
}

export default PhoneButton
